export const Images = ({
  setUploading,
  loading,
}: {
  setUploading: (value: React.SetStateAction<boolean>) => void;
  loading: boolean;
}) => {
  console.log("app/components/Images");

  const [data, setData] = React.useState<{ Key: string }[] | null>(null);

  React.useEffect(() => {
    async function bop() {
      console.log("fetch(/api/documents)");
      const f = await fetch("/api/documents").then((res) => res.json());
      setData(f);
    }
    bop();
    console.log("asdf");
  }, [loading]);

  const audios = data?.filter(
    (image) =>
      typeof image.Key == "string" &&
      image.Key != "" &&
      !/\//.test(image.Key) &&
      /\.wav/.test(image.Key)
  );
  const images = data?.filter(
    (image) =>
      typeof image.Key == "string" &&
      image.Key != "" &&
      !/\//.test(image.Key) &&
      !/\.wav/.test(image.Key)
  );
  return (
    <>
      {images && (
        <div className="pb-2">
          <h2 className="w-full text-center text-2xl font-bold pb-4">Images</h2>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
            {images.map((image, imageIdx) => (
              <S3Image
                setUploading={setUploading}
                Key={image?.Key ? image.Key : "p" + imageIdx}
                key={image?.Key ? image.Key : "p" + imageIdx}
              />
            ))}
          </div>
        </div>
      )}
      {audios && (
        <div className="gap-2">
          <h2 className="w-full text-center flex text-2xl font-bold p-4">
            Audios
          </h2>
          <div id="audios" className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {audios.map((image, imageIdx) => (
              <S3Audio
                key={image.Key}
                Key={image?.Key ? image.Key : "p" + imageIdx}
              />
            ))}
          </div>
        </div>
      )}
    </>
  );
};

const S3Image = ({
  setUploading,
  Key,
}: {
  setUploading: (value: React.SetStateAction<boolean>) => void;
  Key: string;
}) => {
  const [dd, setDD] = React.useState<string | null>(null);

  React.useEffect(() => {
    async function bo() {
      const da = await fetch(`/api/documents/${Key}`);
      const data = await da.json();
      setDD(data.src);
    }
    bo();
  }, [Key]);
  return dd ? (
    <div
      key={Key}
      className="relative h-auto max-w-full rounded-xl bg-slate-200 justify-center items-center flex p-4"
    >
      <button
        onClick={async () => {
          setUploading(true);
          const da = await fetch(`/api/documents/delete/${Key}`);
          setUploading(false);
        }}
        type="button"
        className="absolute top-1 left-1 bg-white rounded-md p-2 inline-flex items-center justify-center text-gray-400 hover:text-gray-500 hover:bg-gray-100 focus:outline-none focus:ring-2 focus:ring-inset focus:ring-indigo-500"
      >
        <span className="sr-only">Close menu</span>
        <svg
          className="h-6 w-6"
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          stroke="currentColor"
          aria-hidden="true"
        >
          <path
            stroke-linecap="round"
            stroke-linejoin="round"
            stroke-width="2"
            d="M6 18L18 6M6 6l12 12"
          />
        </svg>
      </button>

      <Image
        placeholder="blur"
        blurDataURL="data:image/jpeg;base64,"
        width={100}
        height={100}
        key={Key}
        alt="asshoole"
        src={dd}
      />
    </div>
  ) : null;
};

const S3Audio = ({ Key }: { Key: string }) => {
  const [dd, setDD] = React.useState<string | null>(null);
  React.useEffect(() => {
    async function bo() {
      const da = await fetch(`/api/documents/${Key}`);
      const data = await da.json();
      setDD(data.src);
    }
    bo();
  }, [Key]);
  return dd ? (
    <div
      key={Key}
      className="h-auto max-w-full rounded-lg bg-slate-100 justify-center items-center flex p-4"
    >
      <figure key={Key}>
        <figcaption>Listen to {Key?.slice(10, 20)}:</figcaption>
        <audio src={dd} controls></audio>
        <a href={Key}> Download audio </a>
      </figure>
    </div>
  ) : null;
};
